import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:5001/api/tasks';

function App() {
  const [tasks, setTasks] = useState([]);
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch all tasks
  const fetchTasks = async () => {
    try {
      setLoading(true);
      const response = await axios.get(API_BASE_URL);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  // Add a new task
  const addTask = async (e) => {
    e.preventDefault();
    if (!description.trim()) return;

    try {
      const response = await axios.post(API_BASE_URL, { description });
      setTasks([...tasks, response.data]);
      setDescription('');
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  // Complete a task
  const completeTask = async (id) => {
    try {
      const taskToUpdate = tasks.find(task => task._id === id);
      if (taskToUpdate) {
        await axios.put(`${API_BASE_URL}/${id}`, { 
          ...taskToUpdate, 
          completed: !taskToUpdate.completed 
        });
        fetchTasks(); // Refresh the task list
      }
    } catch (error) {
      console.error('Error completing task:', error);
    }
  };

  // Delete a task
  const deleteTask = async (id) => {
    try {
      await axios.delete(`${API_BASE_URL}/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  // Delete all completed tasks
  const deleteAllCompletedTasks = async () => {
    try {
      const completedTasks = tasks.filter(task => task.completed);
      await Promise.all(
        completedTasks.map(task => axios.delete(`${API_BASE_URL}/${task._id}`))
      );
      fetchTasks(); // Refresh the task list
    } catch (error) {
      console.error('Error deleting completed tasks:', error);
    }
  };

  // Update task priority
  const updateTaskPriority = async (id, newPriority) => {
    try {
      const taskToUpdate = tasks.find(task => task._id === id);
      if (taskToUpdate) {
        await axios.put(`${API_BASE_URL}/${id}`, { 
          ...taskToUpdate, 
          priority: newPriority 
        });
        fetchTasks(); // Refresh the task list
      }
    } catch (error) {
      console.error('Error updating task priority:', error);
    }
  };

  // Get priority label
  const getPriorityLabel = (priority) => {
    switch (priority) {
      case 1: return 'High';
      case 2: return 'Medium';
      case 3: return 'Low';
      default: return 'Unknown';
    }
  };

  // Get priority class
  const getPriorityClass = (priority) => {
    switch (priority) {
      case 1: return 'high-priority';
      case 2: return 'medium-priority';
      case 3: return 'low-priority';
      default: return '';
    }
  };

  // Filter tasks by priority
  const allTasks = tasks.filter(task => 
    task.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const highPriorityTasks = allTasks.filter(task => task.priority === 1 && !task.completed);
  const mediumPriorityTasks = allTasks.filter(task => task.priority === 2 && !task.completed);
  const lowPriorityTasks = allTasks.filter(task => task.priority === 3 && !task.completed);
  const completedTasks = allTasks.filter(task => task.completed);

  // Drag and drop handlers
  const handleDragStart = (e, taskId) => {
    e.dataTransfer.setData('taskId', taskId);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, newPriority) => {
    e.preventDefault();
    const taskId = e.dataTransfer.getData('taskId');
    updateTaskPriority(taskId, newPriority);
  };

  // Calculate statistics
  const totalTasks = tasks.length;
  const completedTaskCount = completedTasks.length;
  const pendingTaskCount = totalTasks - completedTaskCount;

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="App">
      {/* Attractive Heading */}
      <div className="app-heading">
        <h1 className="app-title">PriorityFlow</h1>
        <p className="app-subtitle">Intelligent task management with AI-powered prioritization and drag-and-drop organization</p>
      </div>

      <header className="App-header">
        <h1>AI-Based Priority To-Do List</h1>
        <p>Tasks are automatically prioritized using AI or keyword analysis. Drag and drop tasks between priority levels.</p>
      </header>

      <div className="stats-container">
        <div className="stat-card">
          <div className="stat-value">{totalTasks}</div>
          <div className="stat-label">Total Tasks</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{pendingTaskCount}</div>
          <div className="stat-label">Pending</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{completedTaskCount}</div>
          <div className="stat-label">Completed</div>
        </div>
      </div>

      <main>
        <form onSubmit={addTask} className="task-form">
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter a task (e.g., 'Finish report by Friday')"
            className="task-input"
          />
          <button type="submit" className="add-button">Add Task</button>
        </form>

        <div className="task-form">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search tasks..."
            className="task-input"
          />
        </div>

        {loading ? (
          <p>Loading tasks...</p>
        ) : (
          <div className="tasks-board">
            <div 
              className="priority-column high-priority"
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, 1)}
            >
              <h2>High Priority</h2>
              <ul className="tasks-list">
                {highPriorityTasks.map((task) => (
                  <li 
                    key={task._id} 
                    className={`task-item ${getPriorityClass(task.priority)} ${task.completed ? 'completed' : ''}`}
                    draggable
                    onDragStart={(e) => handleDragStart(e, task._id)}
                  >
                    <div className="task-content">
                      <span className="task-description">{task.description}</span>
                      <span className={`priority-badge ${getPriorityClass(task.priority)}`}>
                        {getPriorityLabel(task.priority)} Priority
                      </span>
                    </div>
                    <div className="task-actions">
                      <button 
                        onClick={() => completeTask(task._id)}
                        className="complete-button"
                      >
                        {task.completed ? 'Undo' : 'Complete'}
                      </button>
                      <button 
                        onClick={() => deleteTask(task._id)}
                        className="delete-button"
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))}
                {highPriorityTasks.length === 0 && (
                  <li className="empty-state">No high priority tasks</li>
                )}
              </ul>
            </div>

            <div 
              className="priority-column medium-priority"
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, 2)}
            >
              <h2>Medium Priority</h2>
              <ul className="tasks-list">
                {mediumPriorityTasks.map((task) => (
                  <li 
                    key={task._id} 
                    className={`task-item ${getPriorityClass(task.priority)} ${task.completed ? 'completed' : ''}`}
                    draggable
                    onDragStart={(e) => handleDragStart(e, task._id)}
                  >
                    <div className="task-content">
                      <span className="task-description">{task.description}</span>
                      <span className={`priority-badge ${getPriorityClass(task.priority)}`}>
                        {getPriorityLabel(task.priority)} Priority
                      </span>
                    </div>
                    <div className="task-actions">
                      <button 
                        onClick={() => completeTask(task._id)}
                        className="complete-button"
                      >
                        {task.completed ? 'Undo' : 'Complete'}
                      </button>
                      <button 
                        onClick={() => deleteTask(task._id)}
                        className="delete-button"
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))}
                {mediumPriorityTasks.length === 0 && (
                  <li className="empty-state">No medium priority tasks</li>
                )}
              </ul>
            </div>

            <div 
              className="priority-column low-priority"
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, 3)}
            >
              <h2>Low Priority</h2>
              <ul className="tasks-list">
                {lowPriorityTasks.map((task) => (
                  <li 
                    key={task._id} 
                    className={`task-item ${getPriorityClass(task.priority)} ${task.completed ? 'completed' : ''}`}
                    draggable
                    onDragStart={(e) => handleDragStart(e, task._id)}
                  >
                    <div className="task-content">
                      <span className="task-description">{task.description}</span>
                      <span className={`priority-badge ${getPriorityClass(task.priority)}`}>
                        {getPriorityLabel(task.priority)} Priority
                      </span>
                    </div>
                    <div className="task-actions">
                      <button 
                        onClick={() => completeTask(task._id)}
                        className="complete-button"
                      >
                        {task.completed ? 'Undo' : 'Complete'}
                      </button>
                      <button 
                        onClick={() => deleteTask(task._id)}
                        className="delete-button"
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))}
                {lowPriorityTasks.length === 0 && (
                  <li className="empty-state">No low priority tasks</li>
                )}
              </ul>
            </div>
          </div>
        )}

        {completedTasks.length > 0 && (
          <div className="completed-tasks-section">
            <h2>
              Completed Tasks ({completedTasks.length})
              <button 
                onClick={deleteAllCompletedTasks}
                className="delete-all-button"
              >
                Delete All
              </button>
            </h2>
            <ul className="tasks-list">
              {completedTasks.map((task) => (
                <li 
                  key={task._id} 
                  className={`task-item ${getPriorityClass(task.priority)} completed`}
                >
                  <div className="task-content">
                    <span className="task-description">{task.description}</span>
                    <span className={`priority-badge ${getPriorityClass(task.priority)}`}>
                      {getPriorityLabel(task.priority)} Priority
                    </span>
                  </div>
                  <div className="task-actions">
                    <button 
                      onClick={() => completeTask(task._id)}
                      className="undo-button"
                    >
                      Undo
                    </button>
                    <button 
                      onClick={() => deleteTask(task._id)}
                      className="delete-button"
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </main>

      {/* Small Footer */}
      <footer className="app-footer">
        <div className="footer-content">
          <div className="footer-text">
            © {new Date().getFullYear()} PriorityFlow - AI-Powered Task Management
          </div>
          <div className="footer-divider">|</div>
          <div className="footer-text">
            Built with ❤️ using MERN Stack
          </div>
          <div className="footer-divider">|</div>
          <div className="footer-text">
            <a href="#" className="footer-link">Privacy Policy</a>
          </div>
          <div className="footer-divider">|</div>
          <div className="footer-text">
            <a href="#" className="footer-link">Terms of Service</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
